import React from 'react'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';
import Card from 'react-bootstrap/Card';
import { useState } from 'react';


function AddRestuarant() {

    const [name, setName] = useState("");
    const [phone, setPhone] = useState("");
    const [addr, setAddr] = useState("");

    const submit = (e) => {
        e.preventDefault();

        const restData={
            name:name,
            phone:phone,
            address:addr
        }
       
        axios.post('http://localhost:5000/add',).then((res) => {

            console.log(" Data Added "+res);
        }).catch((err) => {
            console.log(" Data not Added "+err);
        })
    }

  return (
    <div className="w-full max-w-md mx-auto px-4 py-2">
       
       <Card>

    
        <Card.Header className="flex justify-center items-center h-full">
          Add Restaurant
        </Card.Header>
      

          <Card.Body>
            <Card.Text>
            <Form>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Name</Form.Label>
        <Form.Control type="Name" placeholder="Enter email" onChange={(e)=>setName(e.target.value)}/>
      </Form.Group>

                 
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Phone number</Form.Label>
        <Form.Control type="phone" placeholder="Enter email" onChange={(e)=>setPhone(e.target.value)}/>
      </Form.Group>

                 
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Address</Form.Label>
        <Form.Control type="Address" placeholder="Enter email" onChange={(e)=>setAddr(e.target.value)}/>
      </Form.Group>


      <Button variant="primary" type="submit">
        Submit
      </Button>
    </Form>
            </Card.Text>
          </Card.Body>
        </Card>
    </div>
  )
}

export default AddRestuarant
