import React from 'react'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';
import Card from 'react-bootstrap/Card';
import { useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function UpdateRes() {

    const [name, setName] = useState("");
    const [phone, setPhone] = useState("");
    const [addr, setAddr] = useState("");

    const UpdataData=(e)=>{
        e.preventDefault();

        const {id}=useParams();

        const updatedData={
            name:name,
            phone:phone,
            address:addr
        }
        
        axios.put(`http://localhost:8000/resturant/update/${id}`,updatedData).then((res) => {
            console.log(" Data Updated "+res);
        }).catch((err) => {
            console.log(" Data not Updated "+err);
        })
    }

  return (
    <div className="w-full max-w-md mx-auto px-4 py-2">
       
    <Card>

 
     <Card.Header className="flex justify-center items-center h-full">
       Add Restaurant
     </Card.Header>
   

       <Card.Body>
         <Card.Text>
         <Form>

   <Form.Group className="mb-3" controlId="formBasicEmail">
     <Form.Label>Name</Form.Label>
     <Form.Control type="Name" placeholder="Name" onChange={(e)=>setName(e.target.value)}/>
   </Form.Group>

              
   <Form.Group className="mb-3" controlId="formBasicEmail">
     <Form.Label>Phone number</Form.Label>
     <Form.Control type="phone" placeholder="Email" onChange={(e)=>setPhone(e.target.value)}/>
   </Form.Group>

              
   <Form.Group className="mb-3" controlId="formBasicEmail">
     <Form.Label>Address</Form.Label>
     <Form.Control type="Address" placeholder="Address" onChange={(e)=>setAddr(e.target.value)}/>
   </Form.Group>

 <div className="flex justify-center mt-4">
   <Button variant="primary" type="submit" /*onClick={submit}*/ className="flex justify-center">
     Submit
   </Button>
</div>

 </Form>
         </Card.Text>
       </Card.Body>
     </Card>
 </div>
  )
}

export default UpdateRes
