import axios from 'axios';
import React, { useEffect } from 'react'
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import { useState } from 'react';


function ShowAllRes() {

    const [allData, setAllData] =useState([]);
    
    const getAllData=()=>{

        axios.get('http://localhost:8000/resturant/Alldata').then((res) => {
            console.log(res.data);
            setAllData(res.data)
        }).catch((err) => {
            console.log(" Data not Added "+err);
        }
        ) 
    }

useEffect(()=>{
   getAllData();
},[])


const RestDelete=(id)=>{

    axios.delete(`http://localhost:8000/resturant/delete/${id}`).then((res) => {
        console.log(" Data Deleted "+res);
    }).catch((err) => {
        console.log(" Data not Deleted "+err);
    })
}


  return (
    <div>
           <Table responsive="sm">
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Delete & Update</th>
          </tr>
        </thead>
        <tbody>
            {allData.map((data) => (
                <tr key={data._id}>
                <td>{data.name}</td>
                <td>{data.phone}</td>
                <td>{data.address}</td>
                <td><Button variant="danger" onClick={(e)=>{RestDelete(data._id)}}> <i class="bi bi-trash3-fill"></i> Delete</Button>    <a href={`/update/${data._id}`}>
                  <Button variant="success">
                    <i className="bi bi-arrow-counterclockwise"></i> Update
                  </Button>
                </a></td>
                <td> <a href={``}></a></td>
                </tr>
            ))}
        </tbody>
      </Table>
    </div>
  )
}

export default ShowAllRes
