import React from 'react'
import Card from 'react-bootstrap/Card';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useState,useEffect } from 'react';

function ViewData() {

    const{id}=useParams();

    const [oneData, setOneData] = useState([]);

    const getRelavantData=()=>{

        axios.get(`http://localhost:8000/resturant/get/${id}`).then((res) => {
            console.log(" Data get "+res);
            setOneData(res.data)
        }).catch((err) => {
            console.log(" Data not get "+err);
        })
    }

    useEffect(()=>{
        getRelavantData();
    },[])

  return (
    <div>
       
    <Card>

 
     <Card.Header className="flex justify-center items-center h-full">
         Restaurant Details
     </Card.Header>
   

       <Card.Body>
         <Card.Text>
            
           <h3></h3> <h1>{oneData._id}</h1>
            <h1>{oneData.name}</h1>
            <h1>{oneData.phone}</h1>
            <h1>{oneData.address}</h1>
           
         </Card.Text>
       </Card.Body>
     </Card>
 </div>
  )
}

export default ViewData
